local ptrn = ""
local clrs = {
    c0 = colors.black,
    c1 = colors.red,
    c2 = colors.cyan,
    c3 = colors.magenta,
    c4 = colors.lightBlue
}

term.setBackgroundColor(colors.black)
term.clear()
local x = 1
local y = 1
for i = 1,ptrn:len() do
    if x > 51 then
        x = 1
        y = y + 1
        sleep(0.085)
    end
    paintutils.drawPixel(x,y,clrs["c" .. ptrn:sub(i,i)])
    x = x + 1
end
sleep(0.8)
for _ = 1, 19 do
    term.scroll(1)
    sleep(0.085)
end
